export default class permissions {
    constructor() {
    }
    static ACCESSIBILITY = PermissionsApi._ACCESSIBILITY;
    static FLOAT = PermissionsApi._FLOAT;
    static RECORDSCREEN = PermissionsApi._RECORDSCREEN;
    static requestPermissionAsync(tag, timeout) {
        return PermissionsApi.requestPermissionAsync(tag, timeout);
    }
    static requestPermission(tag, timeout) {
        return PermissionsApi.requestPermission(tag, timeout);
    }
    static checkPermissionAsync(tag) {
        return PermissionsApi.checkPermissionAsync(tag);
    }
    static checkPermission(tag) {
        return PermissionsApi.checkPermission(tag);
    }
}
