export default class persistence {
    constructor() {
    }
    static saveAsync(name, key, value) {
        return PersistenceApi.saveAsync(name, key, value);
    }
    static save(name, key, value) {
        return PersistenceApi.save(name, key, value);
    }
    static rmAsync(name, key) {
        return PersistenceApi.rmAsync(name, key);
    }
    static rm(name, key) {
        return PersistenceApi.rm(name, key);
    }
    static getAsync(name, key, type) {
        return PersistenceApi.get(name, key, type);
    }
    static get(name, key, type) {
        return PersistenceApi.get(name, key, type);
    }
}
