export default class plugins {
    constructor() {
    }
    static installAsync(path) {
        return PluginsApi.installAsync(path);
    }
    static install(path) {
        return PluginsApi.install(path);
    }
    static startAsync() {
        return PluginsApi.startAsync();
    }
    static start() {
        return PluginsApi.start();
    }
    static uninstallAsync(pkg) {
        return PluginsApi.checkPermissionAsync(pkg);
    }
    static uninstall(pkg) {
        return PluginsApi.checkPermission(pkg);
    }
}
