export default class recordscreen {
    constructor() {
    }
    static requestPermissionAsync(timeout) {
        return RecordScreenApi.requestPermissionAsync(timeout);
    }
    static requestPermission(timeout) {
        return RecordScreenApi.requestPermission(timeout);
    }
    static checkPermissionAsync() {
        return RecordScreenApi.checkPermissionAsync();
    }
    static checkPermission() {
        return RecordScreenApi.checkPermission();
    }
    static captureScreenAsync(timeout, x, y, ex, ey) {
        return RecordScreenApi.captureScreenAsync(timeout, x, y, ex, ey);
    }
    static captureScreen(timeout, x, y, ex, ey) {
        return RecordScreenApi.captureScreen(timeout, x, y, ex, ey);
    }
}
