export default class root {
    constructor() {
    }
    static requestPermissionAsync(timeout) {
        return RootApi.requestPermissionAsync(timeout);
    }
    static requestPermission(timeout) {
        return RootApi.requestPermission(timeout);
    }
    static checkPermissionAsync() {
        return RootApi.checkPermissionAsync();
    }
    static checkPermission() {
        return RootApi.checkPermission();
    }
    static execAsync(command) {
        return RootApi.exec(command);
    }
    static exec(command) {
        return RootApi.exec(command);
    }
    static parseEventAsync(str) {
        return RootApi.parseEventAsync(str);
    }
    static parseEvent(str) {
        return RootApi.parseEvent(str);
    }
}
