export default class colors {
    constructor() {
    }
    static GREEN = ColorsApi._GREEN;
    static RED = ColorsApi._RED;
    static BLUE = ColorsApi._BLUE;
    static BLACK = ColorsApi._BLACK;
    static WHITE = ColorsApi._WHITE;
    static getRgbAsync(bitmap, x, y) {
        return ColorsApi.getRgbAsync(bitmap, x, y);
    }
    static getRgb(bitmap, x, y) {
        return ColorsApi.getRgb(bitmap, x, y);
    }
    static findMultiColors(bitmap, firstColor, paths, options) {
        return ColorsApi.findMultiColors(bitmap, firstColor, paths, options);
    }
}
