declare var PersistenceApi: any
import {Class} from "types";
export default class persistence {
    constructor() {
    }

    public static saveAsync(name: string, key: string, value: any): Promise<void> {
        return PersistenceApi.saveAsync(name,key,value);
    }

    public static save(name: string, key: string, value: any): void {
        return PersistenceApi.save(name,key,value);
    }
    public static rmAsync(name: string, key: string): Promise<void> {
        return PersistenceApi.rmAsync(name,key);
    }

    public static rm(name: string, key: string): void {
        return PersistenceApi.rm(name,key);
    }

    public static getAsync(name: string, key: string,type :Class<any>): Promise<any> {
        return PersistenceApi.get(name,key,type);
    }

    public static get(name: string, key: string,type :Class<any>): any {
        return PersistenceApi.get(name,key,type);
    }
}