declare var PluginsApi: any
export default class plugins {
    constructor() {
    }

    public static installAsync(path: string): Promise<boolean> {
        return PluginsApi.installAsync(path);
    }

    public static install(path: string): boolean {
        return PluginsApi.install(path);
    }

    public static startAsync(): Promise<void> {
        return PluginsApi.startAsync();
    }

    public static start(): void {
        return PluginsApi.start();
    }


    public static uninstallAsync(pkg: string): Promise<boolean> {
        return PluginsApi.checkPermissionAsync(pkg);
    }

    public static uninstall(pkg: string): boolean {
        return PluginsApi.checkPermission(pkg);
    }
}