declare var ToolWindowApi: any

interface IConsoleClient {
    show(): void;
    log(log: string): void;
    clear(): void;
    hide(): void;
    setTouch(enabled: boolean): void;
}
interface IToolWindowClient {
    show(): void;
    hide(): void;
    cancel(): void;
}

export default class toolwindow {
    constructor() {
    }

    public static FloatingLog(): IConsoleClient {
        return ToolWindowApi.FloatingLog();
    }
    public static FloatingBall(): IToolWindowClient {
        return ToolWindowApi.FloatingBall();
    }
}