import {Callback} from "types";

declare var WebViewApi: any
export default class webview {
    constructor() {
    }

    public static ui(callback: Callback): void {
        return WebViewApi.inject(callback);
    }
    public static window(methodName: string): any;
    public static window(methodName: string, ...args: any[]): any;
    public static window(methodName: string, ...args: any[]): any {
        return WebViewApi.window(methodName, ...args);
    }
    public static document(methodName: string): any;
    public static document(methodName: string, ...args: any[]): any;
    public static document(methodName: string, ...args: any[]): any {
        return WebViewApi.document(methodName, ...args);
    }
    public static runWebView(id: string, http: string): void {
        return WebViewApi.runWebView(id, http);
    }


}