declare var ColorsApi: any
import {Bitmap, Class, IntArray} from "types";
export default class colors {
    constructor() {
    }
    public static readonly GREEN:number = ColorsApi._GREEN
    public static readonly RED:number = ColorsApi._RED
    public static readonly BLUE:number = ColorsApi._BLUE
    public static readonly BLACK:number = ColorsApi._BLACK
    public static readonly WHITE:number = ColorsApi._WHITE
    public static getRgbAsync(bitmap: Bitmap, x: number, y: number): Promise<IntArray> {
        return ColorsApi.getRgbAsync(bitmap, x,y);
    }

    public static getRgb(bitmap: Bitmap, x: number, y: number): IntArray {
        return ColorsApi.getRgb(bitmap, x,y);
    }

    public static findMultiColors(bitmap: Bitmap,firstColor: string, paths:any, options: any): any {
        return ColorsApi.findMultiColors(bitmap, firstColor,paths,options);
    }

}